control.onControlLoad:
g5.p1 = ee.getControl("divRoot").p1;

//分页处理
g5.pageSize = 15;
g5.pageCurrent = 1;
g5.pageCount = 1;
g5.pageJump = function (pageNumber) {
    if (pageNumber < 1) pageNumber = 1;
    if (pageNumber > g5.pageCount) {
        pageNumber = g5.pageCount;
    }
    if (g5.pageCurrent != pageNumber) {
        g5.pageCurrent = pageNumber;
        if (g5.pageCurrent > 0) {
            query();
        }
    }
}

query();

control.onClick:
var s = prompt("请输入需要增加的搜索关键字","");
if (s != null) {
    addNew.text = s;
    addNew.onComplete = function() {
        query();
    }
    addNew();
} else {
}

if (!g5.tableClick.valid) {
    alert("请选择有效的记录进行操作");
    return;
}

del.recordId = query.dataset.getData(0,g5.tableClick.y);
del.onComplete = function() {
    query();
}
del();

if (!g5.tableClick.valid) {
    alert("请选择有效的记录进行操作");
    return;
}

var dist = prompt("请输入向上移动的步数","1");
if (dist != null) {
    if (isNaN(dist)) {
        alert("步数必须是整数");
        return;
    }
    dist = parseInt(dist);
    if (dist<=0) {
        alert("步数必须是大于0的整数");
        return;
    }
} else {
    return;
}
moveUp.recordId = query.dataset.getData(0,g5.tableClick.y);
moveUp.moveUpDist = dist;
moveUp.onComplete = function() {
    query();
}
moveUp();

if (!g5.tableClick.valid) {
    alert("请选择有效的记录进行操作");
    return;
}

var dist = prompt("请输入向下移动的步数","1");
if (dist != null) {
    if (isNaN(dist)) {
        alert("步数必须是整数");
        return;
    }
    dist = parseInt(dist);
    if (dist<=0) {
        alert("步数必须是大于0的整数");
        return;
    }
} else {
    return;
}
moveDown.recordId = query.dataset.getData(0,g5.tableClick.y);
moveDown.moveDownDist = dist;
moveDown.onComplete = function() {
    query();
}
moveDown();

