var v_onClick = new Array();
v_style.push(new g5.BStyle("","#e8f5fe","#d2ecfc","简体中文","简体中文","简体中文","#1da1f2","#1da1f2","#1da1f2",""));
v_onClick.push(function (senderBtn, event) {
});
