    alert("系统注意到您输入了密码，修改密码必须本人操作，您无权在这里修改他人的密码，所以您输入的密码会被忽略。 ");
}
if (companyId != "") {
    alert("您输入companyId是不必要的，系统将忽略您的输入");
}
if (nickName.length < 2) {
    alert("修改后的昵称长度不能少于2个字符或者汉字");
    return;
}

