sql = "exec userFreeze " +
    userId + "," +
    disableUserId + "," +
    operation;
sqlResult = db.executeQuery("DB", sql); 
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
db.read(sqlResult);
if (db.getInt32(sqlResult, "errorCode") != 0) {
    eio.appendString8("F");
    eio.appendString8(db.getString(sqlResult, "errorString"));
    return;
}

eio.appendString8("T");
eio.appendString8(db.getString(sqlResult, "errorString"));

