    s = "<font color='#C00000'>封号</font>";
} else if (s == "FREEZE") {
    s = "<font color='#C00000'>冻结</font>";
} else if (s == "DelIn") {
    s = "<font color='#C00000'>删号冷却期</font>";
} else if (s == "Del") {
    s = "<font color='#C00000'>销号</font>";
} else if (s == "Private") {
    s = "<font color='#C00000'>私密</font>";
}
ee.setText(sender, s);

