if (searchText != "") {
    condition +=
        " (" +
        "(dbo.userInfo.userName like '%" + db.encode(searchText) + "%') or " +
        "(dbo.userInfo.mobile like '%" + db.encode(searchText) + "%') or " +
        "(dbo.userInfo.nickName like '%" + db.encode(searchText) + "%') or " +
        "(dbo.userInfo.note like '%" + db.encode(searchText) + "%') " +
        ") ";
}
sql =
    "SELECT count(*) as c " +
    "FROM dbo.userInfo " +
    (condition=="" ? "":"WHERE " + condition);
sqlResult = db.executeQuery("DB", sql);   
db.read(sqlResult);
var count = db.getInt32(sqlResult, "c");

sql= 
    "SELECT * " +
    "FROM dbo.userInfo " +
    (condition=="" ? "":"WHERE " + condition) +
    "ORDER BY dbo.userInfo.id DESC ";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
