control.onCreate:
sender.a1 = ee.getControl("panel1");
sender.a2 = ee.getControl("panel6");
sender.a3 = ee.getControl("panel31");
sender.a4 = ee.getControl("panel43");
sender.a5 = ee.getControl("panel51");
sender.a6 = ee.getControl("panel58");

sender.currentPageName = "";
sender.changePage = function(pageName) {
    if (this.currentPageName == "a2") {
        ee.setVisible(this.a2, false);
    } else if (this.currentPageName == "a3") {
        ee.setVisible(this.a3, false);
    } else if (this.currentPageName == "a4") {
        ee.setVisible(this.a4, false);
    } else if (this.currentPageName == "a5") {
        ee.setVisible(this.a5, false);
    } else if (this.currentPageName == "a6") {
        ee.setVisible(this.a6, false);
    }
    this.currentPageName = pageName;
    if (this.currentPageName == "a2") {
        ee.setVisible(this.a2, true);
    } else if (this.currentPageName == "a3") {
        ee.setVisible(this.a3, true);
    } else if (this.currentPageName == "a4") {
        ee.setVisible(this.a4, true);
    } else if (this.currentPageName == "a5") {
        ee.setVisible(this.a5, true);
    } else if (this.currentPageName == "a6") {
        ee.setVisible(this.a6, true);
    }
    g5.tiledV(this,0,0,0);
}

sender.style.borderRadius = "5px";
sender.style.borderWidth = "1px";
sender.style.borderColor = "rgb(29,161,242)";
sender.style.borderStyle = "solid";
sender.style.boxSizing="border-box";
sender.style.backgroundColor = "rgba(29,161,242,0.05)";

sender._input.addEventListener("keydown", function(event) {
    if (event.keyCode==13) {
        ee.getControl("edit8")._input.focus();
        event.preventDefault();
        return false;
    }
});

