control.onClick:
var p1 = ee.getEditText(ee.getControl("edit11"));
var p2 = ee.getEditText(ee.getControl("edit12"));
if (p1!=p2) {
    g5.alert1("密码错误", "您两次输入的密码不一致，请重新输入");
    return;
}

regV2.userName = checkUserName.userName;
regV2.nickName = checkNickName.nickName;
regV2.password = p1;
regV2.onComplete = function() {
    g5.alert1("注册成功", "欢迎加入拳头聊天室！", 
        function() {
            login.username = regV2.userName;
            login.password = regV2.password;
            login.param = "?p=1&u=" + escape(regV2.userName);
            login();
        },
        function() {
            login.username = regV2.userName;
            login.password = regV2.password;
            login.param = "?p=1&u=" + escape(regV2.userName);
            login();
        }
    );
}
regV2();

