if (r.length>0) {
    eio.appendString8("F");
    eio.appendString16("账号名错误：" + r);
    return;
}

//check nickName
var chkNickName = function(nickName) {
    if ((nickName.length<2) || (nickName.length>20)) {
        return "用户名的长度不能小于2或者大于20";
    }
    for (var i=0; i<nickName.length; i++) {
        var ch = nickName[i];
        if ((ch>="a") &amp;&amp; (ch<="z")) continue;
        if ((ch>="A") &amp;&amp; (ch<="Z")) continue;
        if ((ch>="0") &amp;&amp; (ch<="9")) continue;
        if (ch=="_") continue;
        if (ch.charCodeAt(0)>256) continue;
        return "账号名中存在非英文、数字或者汉字字符";
    }
    return "";
}
var r = chkNickName(nickName);
if (r.length>0) {
    eio.appendString8("F");
    eio.appendString16("用户名错误：" + r);
