initLButton = function(button,text,dy,icon,size,dx) {
    if (eb.isEmpty(size)) size = "24 24";
    if (eb.isEmpty(dx)) dx = "0";
    ee.setText(button.status1, "<svg viewBox='"+dx+" "+dy+" "+size+"' style='height: 1.75rem;color: rgba(0,0,0,1.00);fill: currentcolor;vertical-align:middle;'>"+ icon +"</svg>  " + text);
    ee.setTextColor(button.status1, "rgb(0,0,0)");
    ee.setText(button.status2, "<svg viewBox='"+dx+" "+dy+" "+size+"' style='height: 1.75rem;color: rgba(29,161,242,1.00);fill: currentcolor;vertical-align:middle;'>"+ icon +"</svg>  " + text);
    ee.setTextColor(button.status2, "rgb(29,161,242)");
    ee.setText(button.status3, "<svg viewBox='"+dx+" "+dy+" "+size+"' style='height: 1.75rem;color: rgba(29,161,242,1.00);fill: currentcolor;vertical-align:middle;'>"+ icon +"</svg>  " + text);
    ee.setTextColor(button.status3, "rgb(29,161,242)");
}



function addImageOnLoad(na, img) {
    if (!eb.isEmpty(img)) {
        if (eb.isEmpty(img.sw)) return;
    }
    if (eb.isEmpty(img)) img = this;
    if (!img.parentNode) return;
    if (!img.parentNode.style) return;

    var div = img.parentNode;
    if (div.c_imgOld) {
        div.removeChild(div.c_imgOld);
        div.c_imgOld = undefined;
    }

    var imgHeight;
    var ratio;
    var firstLoad = false;
    var imgMode = img.parentNode.imgMode ? img.parentNode.imgMode: "trim";
    if (eb.isEmpty(img.sw)) {
        img.sw = img.width;
        img.sh = img.height;
        firstLoad = true;
    }
    var rect = eb.getDivRectSetting(img.parentNode);
    if (imgMode == "trim") {
        if (rect.w / img.sw > rect.h / img.sh) {
            ratio = rect.w / img.sw;
            img.width = rect.w;
            img.height = parseInt(img.sh * ratio + 1);
            img.style.left = "0px";
            img.style.top = parseInt(rect.h / 2 - img.height / 2) + "px";
        } else {
            ratio = rect.h / img.sh;
            img.width = parseInt(img.sw * ratio + 1);
            img.height = rect.h;
            img.style.left = parseInt(rect.w / 2 - img.width / 2) + "px";
            img.style.top = "0px";
        }
    } else if (imgMode == "fill") {
        if (rect.w / img.sw < rect.h / img.sh) {
            ratio = rect.w / img.sw;
            img.width = rect.w;
            img.height = parseInt(img.sh * ratio);
            img.style.left = "0px";
            img.style.top = parseInt(rect.h / 2 - img.height / 2) + "px";
        } else {
            ratio = rect.h / img.sh;
            img.width = parseInt(img.sw * ratio + 1);
            img.height = rect.h;
            img.style.left = parseInt(rect.w / 2 - img.width / 2) + "px";
            img.style.top = "0px";
        }
    } else if (imgMode == "stretch") {
        img.width = rect.w;
        img.height = rect.h;
        img.style.left = "0px";
        img.style.top = "0px";
    } else if (imgMode == "center") {
        img.style.left = parseInt(rect.w / 2 - img.sw / 2) + "px";
        img.style.top = parseInt(rect.h / 2 - img.sh / 2) + "px";
    } else if (imgMode == "normal") {
        img.style.left = "0px";
        img.style.top = "0px";
    } else {
        img.style.left = "0px";
        img.style.top = "0px";
    }
    if (firstLoad) {
        if (!eb.isEmpty(img.parentNode.onImageLoad)) {
            img.parentNode.onImageLoad(img.parentNode);
        }
    }
}

ee.setImage = function (div, imgUrl, imgMode, alt, title) {
    if (!eb.isEmpty(div.img)) {
        if (eb.isEmpty(alt)) alt = div.img.alt;
        if (eb.isEmpty(title)) title = div.img.title;
        if (eb.isEmpty(imgMode)) imgMode = div.imgMode;
        if (eb.isEmpty(imgUrl)) imgUrl = "url:" + div.img.src;
        if (div.c_imgOld) {
            div.removeChild(div.c_imgOld);
            div.c_imgOld = undefined;
        }
        div.c_imgOld = div.img;
        div.img = undefined;
    }
    if (eb.isEmpty(imgUrl)) imgUrl = "";
    if (eb.isEmpty(imgMode)) imgMode = "trim";
    if (eb.isEmpty(alt)) alt = "";
    if (eb.isEmpty(title)) title = "";
    if (imgUrl.length<=0) {
        if (div.c_imgOld) {
            div.removeChild(div.c_imgOld);
            div.c_imgOld = undefined;
        }
    }
    if (eb.isEmpty(imgUrl)) return;
    if (eb.isEmpty(imgMode)) imgMode = "trim";
    if (eb.isEmpty(alt)) alt = "";
    if (eb.isEmpty(title)) title = "";
    var cmd = "";
    if (imgUrl.length >= 4) {
        cmd = imgUrl.substring(0, 4);
    }
    if (cmd == "url:") {
        imgUrl = imgUrl.substring(4);
    } else if (imgUrl.length<=0) {
    } else {
        //imgUrl = "eeImage.aspx?_image_Url_=" + imgUrl;
        if (ee.relatedCache.length > 0) {
            imgUrl = ee.relatedCache + "../cache" + imgUrl;
        } else {
            imgUrl = "cache" + imgUrl;
            if (ee.pureServer) imgUrl = "/" + imgUrl;
        }
    }
    div.img = document.createElement("img");
    div.imgMode = imgMode;
    div.img.ondragstart = function () { return false; };
    div.img.alt = alt;
    div.img.title = title;
    div.img.style.position = "absolute";
    var rect = eb.getDivRectSetting(div);
    eb.setImgRect(div.img, rect.w, rect.h, null, null);
    div.img.onload = addImageOnLoad;
    div.img.src = imgUrl;
    if (div.childNodes.length > 0) {
        div.insertBefore(div.img, div.childNodes[0]);
    } else div.appendChild(div.img);
}

ee.setOnClick = function (div, func) {
    div.eeOnClick = func;
    eb.addEvent(div, "click", function (event) {
        if (div.eeOnClick) {
            div.eeOnClick(div, event);
        }
    }, false);
}

ee.setOnClick = function (div, func) {
    div.eeOnClick = func;
    if ("ontouchstart" in window) {
        eb.addEvent(div, "touchstart", function (event) {
            div.eeMouseDownX = event.changedTouches[0].screenX;
            div.eeMouseDownY = event.changedTouches[0].screenY;
            div.eeTouchStartTime = new Date();
        }, false);
        eb.addEvent(div, "touchend", function (event) {
            var d = Math.abs(event.changedTouches[0].screenX - div.eeMouseDownX);
            d += Math.abs(event.changedTouches[0].screenY - div.eeMouseDownY);
            if ((d < 20) &amp;&amp; ((new Date()).getTime() - div.eeTouchStartTime.getTime()<300)) {
                if (div.eeOnClick) {
                    //setTimeout(function () { //?
                    div.eeOnClick(div, event);
                    //}, 1);
                }
            }
        }, false);
        eb.addEvent(div, "touchmove", function (event) {
        }, false);
    } else {
        eb.addEvent(div, "click", function (event) {
            if (div.eeOnClick) {
                div.eeOnClick(div, event);
            }
        }, false);
    }
}

function addText(div, text, hAlign, vAlign, font, size, color, weight) {
    var div1 = document.createElement("div");
    div1.style.position = "absolute";
    div1.style.overflow = "hidden";
    var table = document.createElement("table");
    var row = table.insertRow();
    var cell = row.insertCell();
    table.style.position = "absolute";
    table.style.whiteSpace = "pre-wrap";
    cell.style.fontFamily = font;
    cell.style.fontSize = size + "pt";
    cell.style.fontWeight = weight;
    if (color.length == 6) {
        cell.style.color = "#" + color;
    } else {
        cell.style.color = color;
    }
    cell.style.textAlign = hAlign;
    cell.style.verticalAlign = vAlign;
    cell.innerHTML = text;
    div.label = table;
    div.label.hAlign = hAlign;
    div.label.vAlign = vAlign;
    if (div.childNodes.length <= 0) {
        div.appendChild(div1);
    } else if (eb.isEmpty(div._input)) {
        div.appendChild(div1);
    } else {
        div.insertBefore(div1, div._input);
    }
    div1.appendChild(table);
    var r = eb.getDivRectSetting(div);
    ee.setRect(div, r.x, r.y, r.w, r.h, true);
}

ee.setOnMouseEnter = function (div, func) {
    div.eeOnMouseEnter = func;
    eb.addEvent(div, "mouseenter", function(e) {
        div.eeOnMouseEnter(div, e);
    }, false);
    eb.addEvent(div, "mouseover", function(e) {
        div.eeOnMouseEnter(div, e);
    }, false);
}

ee.setOnMouseLeave = function (div, func) {
    div.eeOnMouseLeave = func;
    eb.addEvent(div, "mouseleave", function(e) {
        div.eeOnMouseLeave(div, e);
    }, false);
    eb.addEvent(div, "mouseout", function(e) {
        div.eeOnMouseLeave(div, e);
    }, false);
}

ee.setAnimationEnd = function(div,func) {
    var getAniEvent = function() {
        var t, el = document.createElement("fakeelement");
        var ani = {
            "transition": "transitionend",
            "OTransition": "oTransitionEnd",
            "MozTransition": "transitionend",
            "WebkitTransition": "webkitTransitionEnd"
        }
        for (t in ani) {
            if (el.style[t] !== undefined){
                return ani[t];
            }
        }
        return "";
    }
    var aniEvent = getAniEvent();
    if (aniEvent == "") return;
    if (div.f_onTransitionEnd) div.removeEventListener(aniEvent, div.f_onTransitionEnd);
    div.addEventListener(aniEvent, func, false);
    div.f_onTransitionEnd = func;
}

