control.onChange:
var edit = sender.parentNode;
var text = ee.getEditText(edit);

if (text.split("\n").length-1>20) {
    var s1 = sender.selectionStart;
    var s2 = sender.selectionEnd;
    text = edit.v_lastText;
    edit.setText(text);
    sender.setSelectionRange(s1-1,s2-1);
} else {
    edit.setText(text);
}
edit.v_lastText = text;

ee.getControl("panel1").check();
var text = text.trim();
var c = ee.getControl("panel53");
if (text.length<180) {
    c.v_color = "#29a5f2";
    c.v_r = "9";
    c.refresh(text.length/200);
    ee.setText(ee.getControl("panel122"), "");    
} else if (text.length<=200) {
    c.v_color = "#ffad1f";
    c.v_r = "12";
    c.refresh(text.length/200);    
    ee.setText(ee.getControl("panel122"), 200-text.length);    
    ee.setTextColor(ee.getControl("panel122"), "#ffad1f");
} else {
    c.v_color = "#e0245e";
    c.v_r = "14";
    c.refresh((text.length-200)/100);    
    ee.setText(ee.getControl("panel122"), 200-text.length);
    ee.setTextColor(ee.getControl("panel122"), "#e0245e");
}

