                        var i = 0;
                        for (i=0; i<items.length; i++) if (items[i]==item) break;
                        if (i>=items.length) return;
                        var h = eb.getDivRectSetting(item).h;
                        ee.removeControl(item);
                        items.splice(i,1);
                        g5.batchMoveY(items, i, -h);
                    });
                    ee.showAni(item, 1);
