    }
}

sender.refreshMyInfo = function() {
    if (g5.currentPageIndex!=15) {
        this.refresh();
        return;
    }
    if (!this.v_userInfo) return;
    if (this.v_userInfo.id != g5.myUserInfo.id) return;
    this.v_userInfo = g5.myUserInfo.clone();
    this.refreshCount();
}

