    if (!sender.v_enabled) return;
    sender.style.backgroundColor = sender.bgColorEnabled;
});
sender.eeMouseDown = function(sender,event,x,y) {
    if (!sender.v_enabled) return;
    sender.style.backgroundColor = sender.bgColorDown;
}
