        (ee.getEditText(ee.getControl("edit8")).length>0) &amp;&amp;
        (ee.getEditText(ee.getControl("edit9")).length>0) &amp;&amp;
        (ee.getEditText(ee.getControl("edit10")).length>0)
    );
}

