    ee.setRect(g5.p3.p8.p2,null,null,null,eb.getDivRectSetting(g5.p3.p8.p2.c_title).h);
    g5.player.stop();
    g5.p3.p8.p2.v_size = 1;
} else if (g5.p3.p8.p2.v_size == 1) {
    g5.tiledV(g5.p3.p8.p2, 0);
