    "   [exUserId],[exUplinkType],[exContentId] " +
    " FROM [twitterG].[dbo].[tContent] " +
    " WHERE " + sql1 + "; ";

sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
eio.appendString8("T");
eio.appendDataSet(sqlResult,"id,reCount,fwCount,likeCount,unlikeCount",
    0, 99999);

taskSetItem.script3:
if (eio.readString8() != "T") {
    updateContents.busy = false;
    return;
}
