seconds = parseInt(seconds);
var a;
if (eb.isEmpty(style)) style = 1;
if (style == 1) {
    if (seconds<60) return (seconds<10 ? "0"+seconds: seconds) + "秒";
    if (seconds<60*60) {
        a = seconds%60;
        return parseInt(seconds/60) + "分" + (a<10 ? "0"+a:a) + "秒";
    } else if (seconds<60*60*24) {
        a = parseInt((seconds%(60*60))/60);
        return parseInt(seconds/60/60) + "小时" + (a<10 ? "0"+a:a) + "分钟";
    } else {
        var time = new Date((new Date()).getTime()-seconds*1000);
        if ((new Date()).getYear() != time.getYear()) {
            return (time.getYear() + 1900) + "年" + (time.getMonth()+1) + "月" + time.getDate() + "日";
        } else {
            return (time.getMonth()+1) + "月" + time.getDate() + "日";
        }
    }
} else {
    if (seconds<60) return (seconds<10 ? "0"+seconds: seconds) + "秒";
    if (seconds<60*60) {
        a = seconds%60;
        return parseInt(seconds/60) + "分" + (a<10 ? "0"+a:a) + "秒";
    } else if (seconds<60*60*24) {
        a = parseInt((seconds%(60*60))/60);
        return parseInt(seconds/60/60) + "小时" + (a<10 ? "0"+a:a) + "分钟";
    } else {
        var time = new Date((new Date()).getTime()-seconds*1000);
        return (time.getHours()<=12 ? "上午"+time.getHours() : "下午"+(time.getHours()-12)) + ":" +
            (time.getMinutes()>=10 ? time.getMinutes() : "0" + time.getMinutes()) + " · " +
            (time.getYear() + 1900) + "年" + (time.getMonth()+1) + "月" + time.getDate() + "日";
    }
}

