var init = function() {
    initCount++;
    if ((!g5.YTReady) &amp;&amp; (initCount<=3)) {
        setTimeout(function() {
            init();
        }, 100);
        return;
    }
    getUserInfo();

    g5.changeP3(-1);

    setInterval(function() {
        g5.refreshMain();
    }, 100);
    setInterval(function() {
        g5.processVideo();
    }, 1000);
    setInterval(function() {
        g5.refreshScroll();
    }, 500);
    setInterval(function() {
        g5.refreshContent();
    }, 100);

    if (!g5.isMobile) eb.processDock();
    g5.refreshScreen();
}

