ee.moveControl = function(c, dx, dy) {
    if (!c) return;
    if ((dx==0) &amp;&amp; (dy==0)) return;
    let r = eb.getDivRectSetting(c);
    ee.setRect(c, dx==0?null:r.x+dx, dy==0?null:r.y+dy, null, null);
}

