    this.color1 = color1;
    this.color2 = color2
    this.color3 = color3;
    this.text1 = text1;
    this.text2 = text2;
    this.text3 = text3;
    this.tColor1 = tColor1;
    this.tColor2 = tColor2;
    this.tColor3 = tColor3;
    this.bdColor = bdColor;
}
