control.onCreate:
ee.setText(sender, g5.lang.getByCn(ee.getText(sender)));
g5.initButtonV3(sender,[
    new g5.BStyle("#1da1f2","#1a91da","#1781c2","","","","","","",""),
    new g5.BStyle("#8ed0f9","#8ed0f9","#8ed0f9","","","","","","","")
