        getDelInTime.onComplete = function(timeRemain) {
            var s = "";
            var t = "";
            if (timeRemain>3600*24) {
                t+=parseInt(timeRemain/3600/24) + g5.lang.getByTCn(500,"Â§©");
            }
            if (timeRemain>3600) {
                t+=parseInt((timeRemain%(3600*24))/3600) + g5.lang.getByTCn(500,"Â∞èÊó∂");
            }
            t+=parseInt((timeRemain%(3600))/60) + g5.lang.getByTCn(500,"ÂàÜÈíü");
            s += g5.lang.getByCn("ÊÇ®ÁõÆÂâçÂ§Ñ‰∫éË¥¶Âè∑ÂÜ∑Âç¥ÊúüÔºåË∑ùÁ¶ªË¥¶Âè∑Ê∞∏‰πÖÂà†Èô§ËøòÂâ©");
            if (g5.isMobile) {
                s += "\n<spawn style='color:#a06060;font-size:25px'><b>" + t + "</b></spawn>";
            } else {
                s += "\n<spawn style='color:#a06060;font-size:35px'><b>" + t + "</b></spawn>";
            }
            s += "\n" + g5.lang.getByCn("ÊÇ®ÂèØ‰ª•ÈöèÊó∂ÂÅúÊ≠¢Âà†Èô§„ÄÇ");
            ee.setText(sender.a2.c_label, s);
        }
        getDelInTime();
    } else {
        this.changePage("a1");
    }
    if (g5.currentPageIndex == 26) {
        g5.notPushHistoryCount++;
    }
    g5.changeP2(-1);
    g5.changeP2(26);
}

