            g5.syncTwitter(v_content,v_reContent,v_userInfo,v_reUserInfo)
        }
        newTwitter();
    }
    g5.changeP2(30);
    ee.getControl("panel51").v_photos = new Array();
    g5.refreshPhoto(ee.getControl("panel51"));
}

