    var oldPos = sender.currentCurPos;
    sender.currentCurPos = sender.getCurPosAbs();
    if (oldPos!=sender.currentCurPos) {
        if (sender.onCurPosChange) {
            sender.onCurPosChange(sender,oldPos,sender.currentCurPos);
        }
    }
}

