            eb.processDock();
            ee.setVisible(sender.parentNode, true);
            ee.showAni(ee.getControl("panel10"),0);
        }
        showFansList();               
    } else if (showType == "follow") {

    }
}

sender.loadRecord = function(panel, dataset, index) {
