//public
sender.style.boxSizing="border-box";
sender.style.borderWidth = "1px";
sender.style.borderColor = "#a0a0a0";
sender.style.borderStyle = "solid";
sender.style.borderRadius = "10px";
sender.style.boxShadow = "5px 5px 15px #808080";
