        c.init(2);
    } else {
        c.init(1);
    }
    //nickName
    c = ee.getControl("panel271");
    c.setText(this.v_userInfo.nickName);
    //userName    
    c = ee.getControl("panel270");
    c.setText("@" + this.v_userInfo.userName);
