var getFileName = function(sn, sep) {
    sn = parseInt(sn);
    var f1 = (sn &amp; 0xFF000000) >> 24;
    var f2 = (sn &amp; 0x00fff000) >> 12;
    f1 = f1 ^ 0x59AE;
    f2 = f2 ^ 0xA93;
    sn = ((sn >> 7) | ((sn &amp; 0x7f)<<24)) ^ 0x5BC3A96A;
    return "usr" + f1 + sep + f2 + sep + "p" + sn + ".jpg";
}

