    if (s.charAt(i)!="@") {
        i++;
        continue;
    }
    j = i+1;
    while (j<s.length) {
        if (s.charAt(j)=="|") break;
        j++;
    }
    if (j-i-1>0) {
        var un = s.substr(i+1,j-i-1);
        if (un.toUpperCase()!=userName.toUpperCase())
            alertUsers.push(un);
    }
    i=j;
}

