    " and (dbo.userInfo.status='IDL' or dbo.userInfo.status='DelIn' or dbo.userInfo.status='Private') " + 
    "  AND dbo.tContent.status<>'DEL' " + sql1 +
    "ORDER BY dbo.tContent.id DESC ";

