    "                CASE WHEN uHideList.id IS NULL THEN 0 ELSE 1 END AS uIsHide " +
    "FROM      dbo.follow LEFT OUTER JOIN " +
    "                dbo.userInfo ON dbo.follow.followId = dbo.userInfo.id LEFT OUTER JOIN " +
