    ytbVideo = userInput.substr(pos + "youtu.be/".length, userInput.length);
} else if ((pos=userInput.toLowerCase().indexOf("youtube.com/live/",0))>0) {
    ytbVideo = userInput.substr(pos + "youtube.com/live/".length, userInput.length);
