const len = cs.length;
for (var i = 0; i < 5; i++) {
    code += cs.charAt(Math.floor(Math.random() * len));
}
var r = es.getCaptcha(code, w, h, 15, 70);
session.set("codeEmail", code);

eio.appendString16(r);

