//userName, onComplete
var userName = checkUserName.userName;

var chkUserName = function(userName) {
    if ((userName.length<6) || (userName.length>20)) {
        return "账号名的长度不能小于6或者大于20";
    }
    for (var i=0; i<userName.length; i++) {
        var ch = userName[i];
        if ((ch>="a") &amp;&amp; (ch<="z")) continue;
        if ((ch>="A") &amp;&amp; (ch<="Z")) continue;
        if ((ch>="0") &amp;&amp; (ch<="9")) continue;
        if (ch=="_") continue;
        return "账号名只能是英文或者数字";
    }
    return "";
}
var r = chkUserName(userName);
if (r.length>0) {
    g5.alert1("账号错误提示", r);
    return;
}

eio.appendString8(checkUserName.userName);

