sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    return;
}
db.read(sqlResult);
var sn = db.getInt32(sqlResult, "Sn");
newFileName = getFileName(sn, "/");
newFilePathName = "c:\\www\\incoming\\"+ getFileName(sn, "\\");
es.logCommon("upload test 1 file:" + newFilePathName);

