control.onControlLoad:
g5.currentPageIndex = -1;
g5.imgUrl = "/incoming/";
g5.itemsHome = new Array(); //home
g5.itemsPageUser = new Array();

g5.changePage = function(pageIndex) {
    if ((pageIndex<0) || (pageIndex>=4)) return false;
    if (g5.currentPageIndex == pageIndex) return true;
    if (g5.currentPageIndex == 0) {
        ee.setImage(ee.getControl("panel1"), "/twitterG/main/d1.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty2"), 1);
    } else if (g5.currentPageIndex == 1) {
        ee.setImage(ee.getControl("panel2"), "/twitterG/main/d2.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty3"), 1);
    } else if (g5.currentPageIndex == 2) {
        ee.setImage(ee.getControl("panel3"), "/twitterG/main/d3.png", "fill", "", "");
    } else if (g5.currentPageIndex == 3) {
        ee.setImage(ee.getControl("panel4"), "/twitterG/main/d4.png", "fill", "", "");
    }
    if (pageIndex == 0) {
        ee.setImage(ee.getControl("panel1"), "/twitterG/main/d1s.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty2"), 0);
    } else if (pageIndex == 1) {
        ee.setImage(ee.getControl("panel2"), "/twitterG/main/d2s.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty3"), 0);
    } else if (pageIndex == 2) {
        ee.setImage(ee.getControl("panel3"), "/twitterG/main/d3s.png", "fill", "", "");
    } else if (pageIndex == 3) {
        ee.setImage(ee.getControl("panel4"), "/twitterG/main/d4s.png", "fill", "", "");
    }
    g5.currentPageIndex = pageIndex;
    return true;
}

g5.refreshUserInfo = function() {
    var s;
    if (eb.isEmpty(g5.userName)) return;
    s = "<b>" + g5.nickName + "</b><br/>";
    s += "@" + g5.userName;
    ee.setText(ee.getControl("panel31"), s);
    ee.setEditText(ee.getControl("edit2"), g5.nickName);
    ee.setEditText(ee.getControl("edit3"), g5.userNote);
    ee.setEditText(ee.getControl("edit4"), g5.userHomepage);

    if (g5.photo == "") {
        ee.setImage(ee.getControl("panel8"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel29"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel47"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel59"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel14"), "/twitterG/main/user.png");
    } else {
        ee.setImage(ee.getControl("panel8"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel29"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel47"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel59"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel14"), "url:" + g5.imgUrl + g5.photo);
    }
    if (g5.titleBg == "") {
        ee.setImage(ee.getControl("panel58"));
    } else {
        ee.setImage(ee.getControl("panel58"), "url:" + g5.imgUrl + g5.titleBg, "trim");
    }
}

g5.appendItem = function(parent, username, nickname, times, contentId, userPhoto, photo1, text, label1, label2, label3) {
    var hd = "e" + g5.itemsHome.length + "_";
    if (eb.isEmpty(userPhoto)) userPhoto = "";
    if (eb.isEmpty(photo1)) photo1 = "";
    var w = eb.getDivRect(parent).w;
    var y = 0;
    if (g5.itemsHome.length > 0) {
        y = eb.getDivRect(g5.itemsHome[g5.itemsHome.length-1]).y + eb.getDivRect(g5.itemsHome[g5.itemsHome.length-1]).h;
    }
    y += 2; //v-space

    //root:v_userPhoto,v_titleText,v_text,v_btn1,v_btn2,v_btn3,v_btn4,v_label1,v_label2,v_label3
    //v_contentId
    var c;
    c = createPanel(parent, hd+"panel12", "FFFFFF", 0, y, w, 100);
    var root = c;
    root.v_contentId = contentId;
    eb.setDock(c,"disabled","disabled");
    g5.itemsHome.push(c);
    //avata image box
    c = createPanel($g(hd+"panel12"), hd+"panel13", "", w*0.0208728652751423, w*0.026369168356998, w*0.149905123339658, w*0.149905123339658);
    eb.setDock(c,"disabled","disabled");

    //v_userPhoto
    c = createPanel($g(hd+"panel13"), hd+"panel14", "", 0, 0, 79, 77);
    root.v_userPhoto = c;
    if (userPhoto == "") {
        ee.setImage(c,"/twitterG/main/user.png","trim","","")
    } else {
        ee.setImage(c,"url:" + g5.imgUrl + userPhoto,"trim","","")
    }
    eb.setDock(c,"fill","fill");
    eb.setOuter(c,1,1,1,1);
    c = createPanel($g(hd+"panel13"), hd+"panel15", "", 0, 0, 79, 77);
    ee.setImage(c,"/twitterG/main/home/cirMask.png","trim","","")
    eb.setDock(c,"fill","fill");

    //v_titleText
    c = createPanel($g(hd+"panel12"), hd+"panel16", "", w*0.18595825426945, w*0.00405679513184584, w*0.800759013282732, w*0.0872210953346856);
    root.v_titleText = c;
    addText(c, "<b>" + nickname + "</b>@" + username + " - " + times, "left", "middle", "Helvetica", 9, "000000", "normal");
    eb.setFontSizeAuto(c, 22);
    eb.setDock(c,"disabled","disabled");
    //v_text
    c = createPanel($g(hd+"panel12"), hd+"panel17", "", w*0.18595825426945, w*0.0872210953346856, w*0.77988614800759, w*0.0547667342799189);
    root.v_text = c;
    addText(c, text, "left", "top", "黑体", 9, "000000", "normal");
    eb.setFontSizeAuto(c, 25);
    eb.setDock(c,"disabled","disabled");
    eb.processDock();
    var rect = eb.getDivRect(c.label);
    ee.setRect(c, null, null, null, rect.h);
    rect = eb.getDivRect(c);
    var cy = rect.y + rect.h + 0.03*w;

    //photo1
    if (photo1 != "") {
        c = createPanel($g(hd+"panel12"), hd+"panel18", "", w*0.18595825426945, cy, w*0.787476280834915, w*0.474645030425964);
        cy += w*0.474645030425964 + w*0.03;
        eb.setDock(c,"disabled","disabled");
        c = createPanel($g(hd+"panel18"), hd+"panel19", "", 0, 0, 415, 234);
        ee.setImage(c,"url:" + g5.imgUrl + photo1,"trim","","")
        eb.setDock(c,"fill","fill");
        c = createPanel($g(hd+"panel18"), hd+"panel20", "", 0, 0, 415, 234);
        addSkin(c, "/twitterG/skins/bodyMask.png","9x",57,56,19,20,20,18,19,20,20,18);
        eb.setDock(c,"fill","fill");
    }

    //4 button
    c = createPanel($g(hd+"panel12"), hd+"panel21", "", w*0.18595825426945, cy, w*0.0645161290322581, w*0.062);
    root.v_btn1 = c;
    ee.setImage(c,"/twitterG/main/home/e1.png","fill","","")
    eb.setDock(c,"disabled","disabled");
    ee.setOnClick(c,function(sender,event) {
        g5.newPhoto1 = "";
        ee.setEditText(ee.getControl("edit1"), "");
        ee.setImage(ee.getControl("panel52"), "/twitterG/main/empty.png");
        ee.setText(ee.getControl("panel45"), "回复");
        g5.uplinkType = "R";
        g5.uplinkId = sender.parentNode.v_contentId;
        ee.showAni(ee.getControl("panel42"), 0);
    });
    c = createPanel($g(hd+"panel12"), hd+"panel22", "", w*0.402277039848197, cy, w*0.0645161290322581, w*0.062);
    root.v_btn2 = c;
    ee.setImage(c,"/twitterG/main/home/e2.png","fill","","")
    eb.setDock(c,"disabled","disabled");
    c = createPanel($g(hd+"panel12"), hd+"panel23", "", w*0.616698292220114, cy, w*0.0645161290322581, w*0.062);
    root.v_btn3 = c;
    ee.setImage(c,"/twitterG/main/home/e3.png","fill","","")
    eb.setDock(c,"disabled","disabled");
    c = createPanel($g(hd+"panel12"), hd+"panel24", "", w*0.833017077798861, cy, w*0.064516129032258, w*0.062);
    root.v_btn4 = c;
    ee.setImage(c,"/twitterG/main/home/e4.png","fill","","")
    eb.setDock(c,"disabled","disabled");
    //3 label
    c = createPanel($g(hd+"panel12"), hd+"panel3", "", w*0.25426944971537, cy, w*0.111954459203036, w*0.062);
    root.v_label1 = c;
    addText(c, label1, "left", "middle", "黑体", 9, "808080", "normal");
    eb.setFontSizeAuto(c, 30);
    eb.setDock(c,"disabled","disabled");
    c = createPanel($g(hd+"panel12"), hd+"panel4", "", w*0.472485768500949, cy, w*0.12523719165085, w*0.062);
    root.v_label2 = c;
    addText(c, label2, "left", "middle", "黑体", 9, "808080", "normal");
    eb.setFontSizeAuto(c, 30);
    eb.setDock(c,"disabled","disabled");
    c = createPanel($g(hd+"panel12"), hd+"panel5", "", w*0.685009487666034, cy, w*0.123339658444023, w*0.062);
    root.v_label3 = c;
    addText(c, label3, "left", "middle", "黑体", 9, "808080", "normal");
    eb.setFontSizeAuto(c, 30);
    eb.setDock(c,"disabled","disabled");
    //set total height
    rect = eb.getDivRect(root.v_btn1);
    eb.setDivRect(root, null, null, null, rect.y + rect.h + 0.03*w);

    eb.processDock();
}

