    "      SUM(CASE WHEN tContent_1.uplinkType = 'R' THEN 1 ELSE 0 END) AS replyCount," + 
    "      SUM(CASE WHEN tContent_1.uplinkType = 'F' THEN 1 ELSE 0 END)" + 
    "      AS forwardCount" +
    " FROM dbo.tContent LEFT OUTER JOIN " +
    "      dbo.tContent AS tContent_1 ON dbo.tContent.id = tContent_1.uplinkId" +
    " WHERE (dbo.tContent.userId = " + userId + ")" +
    " GROUP BY dbo.tContent.id, dbo.tContent.regDatetime, dbo.tContent.userId," + 
    "      dbo.tContent.[content], dbo.tContent.photo1, dbo.tContent.likeCount " +
    " ORDER BY dbo.tContent.id DESC ";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    eio.appendString8("F");
    es.logWarning("数据库执行错误, sql=" + sql);
    return;
}
eio.appendString8("T");
eio.appendDataSet(sqlResult,"id,mins,userId,content,photo1,replyCount,forwardCount,likeCount", 0, 99999);

