control.onControlLoad:
ee.setAutoScroll($g("divRoot"), "hidden");

eb.addEvent(g5.mainDiv, "scroll", function() {
    var viewPortH = $g("divRoot").offsetHeight;

    var p2h = eb.getDivRect(ee.getControl("panel135")).h;
    if ((p2h-viewPortH-g5.mainDiv.scrollTop) < 800) {
        if (!eb.isEmpty(updateHomepage.data)) {
            if (updateHomepage.data.rowCount>0) {
                updateHomepage();
            }
        }
    }

    var viewPortY = g5.mainDiv.scrollTop;
    var dy = viewPortY - g5.lastScrollY;
    g5.lastScrollY = viewPortY
    var p3h = eb.getDivRect(g5.p3).h;
    p3y = eb.getDivRect(g5.p3).y;
    //eb.setDivRect(g5.p1,null,viewPortY,null,null);
    //eb.setDivRect(g5.p4,null,viewPortY,null,null);
    if (p3h<=viewPortH) {
        eb.setDivRect(g5.p3,null,viewPortY,null,null);
        return;
    }
    var newY = p3y;
    if (dy>0) {
        if (newY+p3h<viewPortY+viewPortH) {
            newY = viewPortY+viewPortH-p3h;
        }
    } else if (dy<0) {
        if (newY>viewPortY) {
            newY = viewPortY;
        }
    }
    if (newY!=p3y) eb.setDivRect(g5.p3,null,newY,null,null);
}, false);


g5.changeP2(0);
g5.changeP3(1);

getUserInfo();
searchUser();
eb.processDock();
setInterval(function() {g5.refreshMain();}, 100);

control.onPageCreate:
initLButton = function(button,text,dy,icon,size,dx) {
    if (eb.isEmpty(size)) size = "24 24";
    if (eb.isEmpty(dx)) dx = "0";
    ee.setText(button.status1, "<svg viewBox='"+dx+" "+dy+" "+size+"' style='height: 1.75rem;color: rgba(0,0,0,1.00);fill: currentcolor;vertical-align:middle;'>"+ icon +"</svg>  " + text);
    ee.setTextColor(button.status1, "rgb(0,0,0)");
    ee.setText(button.status2, "<svg viewBox='"+dx+" "+dy+" "+size+"' style='height: 1.75rem;color: rgba(29,161,242,1.00);fill: currentcolor;vertical-align:middle;'>"+ icon +"</svg>  " + text);
    ee.setTextColor(button.status2, "rgb(29,161,242)");
    ee.setText(button.status3, "<svg viewBox='"+dx+" "+dy+" "+size+"' style='height: 1.75rem;color: rgba(29,161,242,1.00);fill: currentcolor;vertical-align:middle;'>"+ icon +"</svg>  " + text);
    ee.setTextColor(button.status3, "rgb(29,161,242)");
}

control.onCreate:
g5.scrollFunc = function (e) {
    return;
    var direct = 0;
    ee = e || window.event;
    var t1 = document.getElementById("wheelDelta");
    var t2 = document.getElementById("detail");
    if (e.wheelDelta) {//IE/Opera/Chrome  
        t1.value = e.wheelDelta;
    } else if (e.detail) {//Firefox  
        t2.value = e.detail;
    }

    //ScrollText(direct);
}
    //注册事件
if (document.addEventListener) {
    document.addEventListener("DOMMouseScroll", g5.scrollFunc, false);
}//W3C  
window.onmousewheel = document.onmousewheel = g5.scrollFunc; //IE/Opera/Chrome/Safari

control.onClick:
g5.newPhoto1 = "";
ee.setEditText(ee.getControl("edit1"), "");
ee.setImage(ee.getControl("panel52"), "/twitterG/main/empty.png");
ee.setText(ee.getControl("panel45"), "发推");
newTwitter.uplinkType = "N";
newTwitter.uplinkId = 0;
ee.showAni(ee.getControl("panel42"), 0);

var items = sender.parentNode.eeChildren;
for (var i=0; i<items.length; i++) {
    if (eb.isEmpty(items[i].baseColor)) continue;
    if (items[i]==sender) {
        items[i].baseColor="#1fa2f2";
    } else {
        items[i].baseColor="#14171a";
        ee.setTextColor(items[i], items[i].baseColor);
    }
}
g5.mainDiv.scrollTop = 0;
g5.changeP2(0);

