    sender.style.backgroundColor = "#e8f5fe";
    ee.setTextColor(sender, "#1fa2f2");
});
ee.setOnMouseLeave(sender, function(sender,event) { 
    sender.style.backgroundColor = "";
    ee.setTextColor(sender, sender.baseColor);
});
