control.onUploadCompleteC:
newTwitter.photo1 = eio.readString16();
ee.setImage(ee.getControl("panel7"), "url:" + g5.imgUrl + newTwitter.photo1, "trim", "", "");
ee.setText(ee.getControl("panel7"), "");
if (ee.getControl("panel7").style.visibility=="hidden") {
    ee.setVisible(ee.getControl("panel7"), true);
    ee.setRect(ee.getControl("panel1"),null,null,null,eb.getDivRect(ee.getControl("panel1")).h+462-140);
    g5.homepageMoveY(0,462-140);
    eb.processDock();
}

control.onUploadCompleteS:
var pos=newFileName.lastIndexOf("\\");
var filename = newFileName.substring(pos+1);  
eio.appendString16(filename);

control.onUploadStartS:
var sql,sqlResult;  
sql = "select next value for dbo.SEQImageSn as Sn";
sqlResult = db.executeQuery("DB", sql);
if (sqlResult == null) {
    return;
}
db.read(sqlResult);
var sn = db.getInt32(sqlResult, "Sn");
newFileName = "c:\\www\\incoming\\img" + sn + fileExt;

control.onChange:
//var backup = sender.style.height;
//sender.style.height = "";
var dy = sender.scrollHeight - sender.lastHeight;
//sender.style.height = backup;
if (dy==0) return;
sender.lastHeight = sender.scrollHeight;
//adjust interface
ee.setRect(ee.getControl("panel1"),null,null,null,eb.getDivRect(ee.getControl("panel1")).h + dy);
ee.setRect(ee.getControl("edit7"),null,null,null,eb.getDivRect(ee.getControl("edit7")).h + dy);
g5.homepageMoveY(0,dy);
eb.processDock();

control.onClick:
newTwitter.content = ee.getEditText(ee.getControl("edit7"));
if (ee.getControl("panel7").style.visibility=="hidden") {
    newTwitter.photo1 = "";
}
newTwitter.uplinkType = "N";
newTwitter.uplinkId = 0;
newTwitter();

ee.setVisible(ee.getControl("panel7"), false);
ee.setRect(ee.getControl("panel1"),null,null,null,eb.getDivRect(ee.getControl("panel1")).h-(462-140));
g5.homepageMoveY(0,-(462-140));
eb.processDock();
ee.setText(ee.getControl("panel7"), "");
