control.onClick:
var url = prompt("请输入播放地址：", "");
if (url == null) return;
if (url.length<=0) return;
newTwitter.photo1 = "";
var pos;
if ((pos=url.toLowerCase().indexOf("youtube.com/watch?v=",0))>0) {
    //https://www.youtube.com/watch?v=yJG3WMG1dak
    newTwitter.photo1 = url.substr(pos + "youtube.com/watch?v=".length, url.length);
} else if ((pos=url.toLowerCase().indexOf("youtu.be/",0))>0) {
    //https://youtu.be/edESgyDUKDU
    newTwitter.photo1 = url.substr(pos + "youtu.be/".length, url.length);
} else {
    alert("您输入的视频地址不正确");
    return;
}
if ((newTwitter.photo1.length<=8)||(newTwitter.photo1.length>=14)) {
    alert("您输入的视频地址不正确");
    return;
}
var rect = eb.getDivRect(ee.getControl("panel7"));
url = "<iframe width='" + rect.w + "' height='" + rect.h + "' src='https://www.youtube.com/embed/" +
    newTwitter.photo1 + 
    "' frameborder='0' allow='accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture' allowfullscreen></iframe>";
newTwitter.photo1 = "?YTB" + newTwitter.photo1;
ee.setText(ee.getControl("panel7"), url);
ee.setImage(ee.getControl("panel7"), "", "trim", "", "")   
//ee.getControl("panel7").style.pointerEvents = "none";

if (ee.getControl("panel7").style.visibility=="hidden") {
    ee.setVisible(ee.getControl("panel7"), true);
    ee.setRect(ee.getControl("panel1"),null,null,null,eb.getDivRect(ee.getControl("panel1")).h+462-140);
    g5.homepageMoveY(0,462-140);
    eb.processDock();
}

showChat.userName = showUserPage.userName;
showChat.chatId = 0;
if (showChat.userName == g5.userName) {
    alert("自言自语不好吧...");
    return;
}
ee.showAni(ee.getControl("panel116"), 0);

control.onCreate:
sender.style.boxSizing="border-box";
sender.style.borderWidth = "1px";
sender.style.borderColor = "rgb(230,236,240)";
sender.style.borderStyle = "solid";

sender.refresh = function() {
    for (var i=0; i<this.eeChildren.length; i++) {
        if (eb.isEmpty(this.eeChildren[i].selected)) continue;
        if (this.eeChildren[i].selected) {
            ee.setVisible(this.eeChildren[i].rightLine, true);
            this.eeChildren[i].style.backgroundColor = "rgb(245,248,250)";
        } else {
            ee.setVisible(this.eeChildren[i].rightLine, false);
            this.eeChildren[i].style.backgroundColor = "";
        }
    }
}

sender.reset = function() {
    for (var i=0; i<this.eeChildren.length; i++) {
        if (eb.isEmpty(this.eeChildren[i].selected)) continue;
        this.eeChildren[i].selected = false;
    }
    this.refresh();
}

sender.setSelected = function(item) {
    this.reset();
    item.selected = true;
    this.refresh();
}

