    showTopic.lastId = showTopic.data.getData(0, showTopic.data.rowCount - 1);
} else {
    showTopic.lastId = "0";
}
if (eb.isEmpty(showTopic.items)) showTopic.items = new Array();
showTopic.backTrace = eio.readDataSet();
while (true) {
    var tempData = eio.readDataSet();
    if (!showTopic.backTrace.appendDataset(tempData)) break;
}


var i, item;
var root = ee.getControl("panel22"); 
var cy = eb.getDivRect(g5.p4).h;
if (showTopic.needRefresh) {
    for (i=0; i<showTopic.items.length; i++) ee.removeControl(showTopic.items[i]);
    showTopic.items = new Array();
} else {
    if (showTopic.items.length>0) {
        var rect = eb.getDivRect(showTopic.items[showTopic.items.length-1]);
        cy = 2 + rect.y + rect.h;
    }
}

var itemWidth = eb.getDivRect(root).w;
var uType,dType;
for (i=showTopic.backTrace.rowCount-1; i >= 0; i--) {
    if (i==showTopic.backTrace.rowCount-1) {
        uType = " "
    } else uType = "T";
    dType = "T";
    var item = createPanel(root, "page8_" + showTopic.backTrace.getData(0,i), "", 0, cy, itemWidth, 5);
    g5.loadTwitterRecord(item, showTopic.backTrace, i);
    item.v_mode = uType + dType;
    g5.renderTwitter(item);        
    showTopic.items.push(item);
    cy += 0 + eb.getDivRect(item).h;    
}

var scrollY = cy - eb.getDivRect(g5.p4).h;

for (i=0; i< showTopic.data.rowCount; i++) {
    uType = " "; dType = " ";
    if ((i==0) &amp;&amp; (showTopic.backTrace.rowCount>0)) {
        uType="T"; dType = " ";
    }
    if (i==0) dType += "x";
    var item = createPanel(root, "page8_" + showTopic.data.getData(0,i), "", 0, cy, itemWidth, 5);
    g5.loadTwitterRecord(item, showTopic.data, i);
    item.v_mode = uType + dType;
    g5.renderTwitter(item);        
    showTopic.items.push(item);
    cy += 0 + eb.getDivRect(item).h;    
}
eb.setDivRect(ee.getControl("panel135"),null,null,null,cy);
eb.setDivRect(ee.getControl("panel22"),null,null,null,cy);
showTopic.scrollY = scrollY;
eb.processDock();

showTopic.needRefresh = false;
showTopic.busy = false;

var dTime = (new Date()).getTime() - showTopic.startTime;
if (dTime>50) {
    if (g5.currentPageIndex == 7) g5.changeP2(-1);
    g5.changeP2(7);
    g5.mainDiv.scrollTop = showTopic.scrollY;
} else {
    setTimeout(function() { 
        if (g5.currentPageIndex == 7) g5.changeP2(-1);
        g5.changeP2(7);
        g5.mainDiv.scrollTop = showTopic.scrollY;
    }, 50-dTime);
}

