if (root.eeChildren.length>0) {
    rect = eb.getDivRect(root.eeChildren[root.eeChildren.length-1]);
    y = rect.h + rect.y;
}
rect = eb.getDivRect(root);
var rw = rect.w;
var rh = rect.h;
y += rh * 0.05;
var hd = "pch_";
for (var i = 0; i < showChat.dataset.rowCount; i++) {
    var left = true;
    if (parseInt(showChat.dataset.getData(2,i))==parseInt(showChat.myUserId)) {
        left = false;
    }
    //user photo
    var hd1 = "u" + showChat.dataset.getData(0,i);
    if (left) {
        c = createPanel(root, hd+hd1+"panel13", "", parseInt(rw*0.0296), y, parseInt(rw*0.1002),  parseInt(rw*0.1002));
    } else {
        c = createPanel(root, hd+hd1+"panel13", "", parseInt(rw*0.8405), y, parseInt(rw*0.1002),  parseInt(rw*0.1002));
    }
    eb.setDock(c,"disabled","disabled");
    c = createPanel($g(hd+hd1+"panel13"), hd+hd1+"panel14", "", 0, 0, 79, 77);
    var photo;
    if (left) {
        photo = showChat.chatPhoto;
    } else {
        photo = showChat.myphoto
    }
