//process time
if (!eb.isEmpty(g5.itemsHome)) {
    for (var i=0; i<g5.itemsHome.length; i++) {
        var now = parseInt((new Date()).getTime()/1000);    
        var time = now-g5.itemsHome[i].v_startTime;
        if (time>=24*60*60) break;
        time=g5.getMins(time);
        if (!eb.isEmpty(g5.itemsHome[i].v_timeLabel)) {
            if (g5.itemsHome[i].v_timeLabel.innerHTML!=time) {
                g5.itemsHome[i].v_timeLabel.innerHTML = time;
            }
        }
    }
}
if (!eb.isEmpty(g5.itemsPageUser)) {
    for (var i=0; i<g5.itemsPageUser.length; i++) {
        var now = parseInt((new Date()).getTime()/1000);    
        var time = now-g5.itemsPageUser[i].v_startTime;
        if (time>=24*60*60) break;
        time=g5.getMins(time);
        if (!eb.isEmpty(g5.itemsPageUser[i].v_timeLabel)) {
            if (g5.itemsPageUser[i].v_timeLabel.innerHTML!=time) {
                g5.itemsPageUser[i].v_timeLabel.innerHTML = time;
            }
        }
    }
}
if (!eb.isEmpty(showTopic.items)) {
    for (var i=0; i<showTopic.items.length; i++) {
        var now = parseInt((new Date()).getTime()/1000);    
        var time = now-showTopic.items[i].v_startTime;
        if (time>=24*60*60) break;
        time=g5.getMins(time);
        if (!eb.isEmpty(showTopic.items[i].v_timeLabel)) {
            if (showTopic.items[i].v_timeLabel.innerHTML!=time) {
                showTopic.items[i].v_timeLabel.innerHTML = time;
            }
        }
    }
}


