var p3w = eb.getDivRect(g5.p3).w;
if (g5.pageMode == 0) {
    if (cw >= 300+600+400) {
        p1w = 300;
        p2w = 600;
        p3w = 400;
    } else if (cw >= 80+600+400) {
        p1w = 80;
        p2w = 600;
        p3w = 400;
    } else if (cw >= 80+600+330) {
        p1w = 80;
        p2w = 600;
        p3w = 330;
    } else if (cw >= 80+600) {
        p1w = 80;
        p2w = 600;
        p3w = 0;
    } else {
        p1w = 80;
        p2w = cw-80;
        p3w = 0;
    }
} else if (g5.pageMode == 1) {
    if (cw >= 300+600+400) {
        p1w = 300;
        p2w = 400;
        p3w = 600;
    } else if (cw >= 80+600+400) {
        p1w = 80;
        p2w = 400;
        p3w = 600;
    } else if (cw >= 80+300+6000) {
        p1w = 80;
        p2w = 300;
        p3w = 600;
    } else if (cw >= 80+600) {
        p1w = 80;
        p2w = 0;
        p3w = 600;
    } else {
        p1w = 80;
        p2w = 0;
        p3w = cw-80;
    }
}
