control.onControlLoad:
g5.currentPageIndex = -1;
g5.imgUrl = "/incoming/";
g5.itemsHome = new Array(); //home
g5.itemsPageUser = new Array();
g5.newUser = false;

g5.p1 = ee.getControl("panel136");
g5.p2 = ee.getControl("panel135");
g5.p3 = ee.getControl("panel137");
g5.p4 = ee.getControl("panel24");
g5.pageMode = 0;
g5.mainDiv = ee.getControl("panel134");

g5.lastWidth = 0;
g5.refreshMain = function(refresh) {
    var cw = $g("divRoot").offsetWidth;    
    if (eb.isEmpty(refresh)) refresh = false;
    if ((g5.lastWidth == cw)&amp;&amp;(!refresh)) return;
    g5.lastWidth = cw;
    var p1w = eb.getDivRect(g5.p1).w;
    var p2w = eb.getDivRect(g5.p2).w;
    var p3w = eb.getDivRect(g5.p3).w;
    if (g5.pageMode == 0) {
        if (cw >= 300+600+400) {
            p1w = 300;
            p2w = 600;
            p3w = 400;
        } else if (cw >= 80+600+400) {
            p1w = 80;
            p2w = 600;
            p3w = 400;
        } else if (cw >= 80+600+330) {
            p1w = 80;
            p2w = 600;
            p3w = 330;
        } else if (cw >= 80+600) {
            p1w = 80;
            p2w = 600;
            p3w = 0;
        } else {
            p1w = 80;
            p2w = cw-80;
            p3w = 0;
        }
    } else if (g5.pageMode == 1) {
        if (cw >= 300+600+400) {
            p1w = 300;
            p2w = 400;
            p3w = 600;
        } else if (cw >= 80+600+400) {
            p1w = 80;
            p2w = 400;
            p3w = 600;
        } else if (cw >= 80+300+6000) {
            p1w = 80;
            p2w = 300;
            p3w = 600;
        } else if (cw >= 80+600) {
            p1w = 80;
            p2w = 0;
            p3w = 600;
        } else {
            p1w = 80;
            p2w = 0;
            p3w = cw-80;
        }
    }
    var left = parseInt((cw - p1w - p2w - p3w) / 2);
    ee.setRect(g5.p1,left,null,p1w,null);
    ee.setRect(g5.p2,left+p1w,null,p2w,null);
    ee.setRect(g5.p4,left+p1w,null,p2w,null);
    ee.setRect(g5.p3,left+p1w+p2w,null,p3w,null);
    if (refresh) eb.processDock();
}

g5.lastScrollY = g5.mainDiv.scrollTop;
eb.addEvent(g5.mainDiv, "scroll", function() {
    var viewPortH = $g("divRoot").offsetHeight;

    var p2h = eb.getDivRect(ee.getControl("panel135")).h;
    if (p2h>viewPortH) {
        if ((p2h-viewPortH-g5.mainDiv.scrollTop) < 300) {
            if (!eb.isEmpty(g5.datasetHome)) {
                if (g5.datasetHome.rowCount>0) {
                    updateHomepage();
                }
            }
        }
    }

    var viewPortY = g5.mainDiv.scrollTop;
    var dy = viewPortY - g5.lastScrollY;
    g5.lastScrollY = viewPortY
    var p3h = eb.getDivRect(g5.p3).h;
    p3y = eb.getDivRect(g5.p3).y;
    eb.setDivRect(g5.p1,null,viewPortY,null,null);
    eb.setDivRect(g5.p4,null,viewPortY,null,null);
    if (p3h<=viewPortH) {
        eb.setDivRect(g5.p3,null,viewPortY,null,null);
        return;
    }
    var newY = p3y;
    if (dy>0) {
        if (newY+p3h<viewPortY+viewPortH) {
            newY = viewPortY+viewPortH-p3h;
        }
    } else if (dy<0) {
        if (newY>viewPortY) {
            newY = viewPortY;
        }
    }
    if (newY!=p3y) eb.setDivRect(g5.p3,null,newY,null,null);
}, false);

