g5.changePage = function(pageIndex) {
    if ((pageIndex<0) || (pageIndex>=4)) return false;
    if (g5.currentPageIndex == pageIndex) return true;
    if (g5.currentPageIndex == 0) {
        ee.setImage(ee.getControl("panel1"), "/twitterG/main/d1.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty2"), 1);
    } else if (g5.currentPageIndex == 1) {
        ee.setImage(ee.getControl("panel2"), "/twitterG/main/d2.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty3"), 1);
    } else if (g5.currentPageIndex == 2) {
        ee.setImage(ee.getControl("panel3"), "/twitterG/main/d3.png", "fill", "", "");
    } else if (g5.currentPageIndex == 3) {
        ee.setImage(ee.getControl("panel4"), "/twitterG/main/d4.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty5"), 1);
    }
    if (pageIndex == 0) {
        ee.setImage(ee.getControl("panel1"), "/twitterG/main/d1s.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty2"), 0);
    } else if (pageIndex == 1) {
        ee.setImage(ee.getControl("panel2"), "/twitterG/main/d2s.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty3"), 0);
    } else if (pageIndex == 2) {
        ee.setImage(ee.getControl("panel3"), "/twitterG/main/d3s.png", "fill", "", "");
    } else if (pageIndex == 3) {
        ee.setImage(ee.getControl("panel4"), "/twitterG/main/d4s.png", "fill", "", "");
        ee.showAni(ee.getControl("layouty5"), 0);
    }
    g5.currentPageIndex = pageIndex;
    return true;
}

g5.changeP2 = function(pageIndex) {
    //if ((pageIndex<0) || (pageIndex>=7)) return false;
    if (g5.currentPageIndex == pageIndex) return true;
    var root = ee.getControl("panel135");
    if (g5.currentPageIndex == 0) {
        ee.setVisible(g5.p4, false);
        ee.setVisible(ee.getControl("panel34"), false);
        ee.showAni(ee.getControl("panel41"), 1);
    } else if (g5.currentPageIndex == 1) {
        ee.setVisible(g5.p4, false);
        ee.showAni(ee.getControl("panel16"), 1);
    } else if (g5.currentPageIndex == 2) {
        ee.setVisible(g5.p4, false);
        ee.showAni(ee.getControl("panel82"), 1);
    } else if (g5.currentPageIndex == 3) {
        ee.setVisible(g5.p4, false);
        ee.showAni(ee.getControl("panel70"), 1);
    } else if (g5.currentPageIndex == 4) {
        ee.setVisible(g5.p4, false);
        ee.showAni(ee.getControl("panel91"), 1);
    } else if (g5.currentPageIndex == 5) {
        ee.setVisible(g5.p4, false);
        ee.showAni(ee.getControl("panel97"), 1);
    } else if (g5.currentPageIndex == 6) {
        ee.setVisible(g5.p4, false);
        ee.showAni(ee.getControl("layouty5"), 1);
        ee.showAni(ee.getControl("panel116"), 1);
    } else if (g5.currentPageIndex ==7) {
        ee.setVisible(g5.p4, false);
        ee.setVisible(ee.getControl("panel38"), false);
        ee.showAni(ee.getControl("panel22"), 1);
    }
    if (pageIndex == 0) {
        g5.mainDiv.scrollTop = 0;
        g5.pageMode = 0;
        g5.refreshMain(true);
        ee.setVisible(g5.p4, true);
        ee.setVisible(ee.getControl("panel34"), true);
        ee.setRect(ee.getControl("panel34"),0,0,eb.getDivRect(g5.p4).w,null);
        ee.setRect(g5.p4, null,null,null,eb.getDivRect(ee.getControl("panel34")).h);
        ee.setRect(root,null,null,null,eb.getDivRect(ee.getControl("panel41")).h+1);
        ee.showAni(ee.getControl("panel41"), 0);
    } else if (pageIndex == 1) {
        g5.mainDiv.scrollTop = 0;
        g5.pageMode = 0;
        g5.refreshMain(true);
        eb.setDivRect(root,null,null,null,eb.getDivRect(ee.getControl("panel16")).h);
        ee.showAni(ee.getControl("panel16"), 0);
    } else if (pageIndex == 2) {
        g5.mainDiv.scrollTop = 0;
        g5.pageMode = 0;
        g5.refreshMain(true);
        ee.showAni(ee.getControl("panel82"), 0);
    } else if (pageIndex == 3) {
        g5.mainDiv.scrollTop = 0;
        g5.pageMode = 0;
        g5.refreshMain(true);
        ee.showAni(ee.getControl("panel70"), 0);
    } else if (pageIndex == 4) {
        g5.mainDiv.scrollTop = 0;
        g5.pageMode = 0;
        g5.refreshMain(true);
        ee.showAni(ee.getControl("panel91"), 0);
    } else if (pageIndex == 5) {
        g5.mainDiv.scrollTop = 0;
        g5.pageMode = 0;
        g5.refreshMain(true);
        ee.showAni(ee.getControl("panel97"), 0);
    } else if (pageIndex == 6) {
        g5.mainDiv.scrollTop = 0;
        g5.pageMode = 1;
        g5.refreshMain(true);
        ee.showAni(ee.getControl("layouty5"), 0);
        ee.showAni(ee.getControl("panel116"), 0);
    } else if (pageIndex == 7) {
        g5.mainDiv.scrollTop = 0;
        g5.pageMode = 0;
        ee.setVisible(g5.p4, true);
        ee.setVisible(ee.getControl("panel38"), true);
        ee.setRect(ee.getControl("panel38"),0,0,eb.getDivRect(g5.p4).w,null);
        ee.setRect(g5.p4, null,null,null,eb.getDivRect(ee.getControl("panel38")).h);
        g5.refreshMain(true);
        ee.showAni(ee.getControl("panel22"), 0);
    }
    g5.currentPageIndex = pageIndex;
    return true;
}
g5.changeP2(0);


g5.changePage2 = function(pageIndex) {
    if ((pageIndex<0) || (pageIndex>=2)) return false;
    ee.setVisible(ee.getControl("panel132"), false);
    ee.setVisible(ee.getControl("panel133"), false);
    ee.setVisible(ee.getControl("panel128"), false);
    ee.setVisible(ee.getControl("panel129"), false);
    ee.setTextColor(ee.getControl("layoutxitem15"), "#808080");
    ee.setTextColor(ee.getControl("layoutxitem16"), "#808080");
    if (pageIndex == 0) {
        ee.setVisible(ee.getControl("panel132"), true);
        ee.setVisible(ee.getControl("panel128"), true);
        ee.setTextColor(ee.getControl("layoutxitem15"), "#1C94E0");
    } else if (pageIndex == 1) {
        ee.setVisible(ee.getControl("panel133"), true);
        ee.setVisible(ee.getControl("panel129"), true);
        ee.setTextColor(ee.getControl("layoutxitem16"), "#1C94E0");
    }
}
g5.changePage2(1);

g5.refreshUserInfo = function() {
    var s;
    if (eb.isEmpty(g5.userName)) return;
    s = "<b>" + g5.nickName + "</b><br/>";
    s += "@" + g5.userName;
    ee.setText(ee.getControl("panel31"), s);
    ee.setEditText(ee.getControl("edit2"), g5.nickName);
    ee.setEditText(ee.getControl("edit3"), g5.userNote);
    ee.setEditText(ee.getControl("edit4"), g5.userHomepage);

    if (g5.photo == "") {
        ee.setImage(ee.getControl("panel8"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel29"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel47"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel59"), "/twitterG/main/user.png");
        ee.setImage(ee.getControl("panel14"), "/twitterG/main/user.png");
        //ee.setImage(ee.getControl("panel111"), "/twitterG/main/user.png");
    } else {
        ee.setImage(ee.getControl("panel8"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel29"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel47"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel59"), "url:" + g5.imgUrl + g5.photo);
        ee.setImage(ee.getControl("panel14"), "url:" + g5.imgUrl + g5.photo);
        //ee.setImage(ee.getControl("panel111"), "url:" + g5.imgUrl + g5.photo);
    }
    if (g5.titleBg == "") {
        ee.setImage(ee.getControl("panel58"));
    } else {
        ee.setImage(ee.getControl("panel58"), "url:" + g5.imgUrl + g5.titleBg, "trim");
    }
}

g5.appendItem = function(parent, startY, hd, username, nickname, times, contentId, userPhoto, photo1, text, label1, label2, label3,
    reTimes, reContent, rePhoto1, reUserId, reUserName, reNickName, rePhoto) {
    if (eb.isEmpty(userPhoto)) userPhoto = "";
    if (eb.isEmpty(photo1)) photo1 = "";
    var w = eb.getDivRect(parent).w;
    if (eb.isEmpty(startY)) startY = 0;
    y = startY + 0; //v-space

    //16,9,50x50
    //root:v_userPhoto,v_titleText,v_text,v_btn1,v_btn2,v_btn3,v_btn4,v_label1,v_label2,v_label3
    //v_contentId
    var c;
    c = createPanel(parent, hd+"panel12", "FFFFFF", 0, y, w, 100);
    var root = c;
    root.v_contentId = contentId;
    root.style.boxSizing="border-box";
    root.style.borderWidth = "1px";
    root.style.borderColor = "rgb(230,236,240)";
    root.style.borderStyle = "solid";
    root.style.borderTop = "";
    root.style.cursor = "pointer"; 
    eb.setAni2(root,1,"disappear", "rightFlyOut", "null", 0, true);
    eb.setAniEvent(root,1,function(){}, function(sender) {
        g5.deletePage1(sender);
    });
    ee.setOnClick(c,function(sender,event) {
        showTopic.topicId = sender.v_contentId;
        showTopic.needRefresh = true;
        if (g5.currentPageIndex == 7) g5.changeP2(-1);
        g5.changeP2(7);
    });
    ee.setOnMouseEnter(c, function(sender,e){
        sender.style.backgroundColor = "rgb(245,248,250)";
    });
    ee.setOnMouseLeave(c, function(sender,e){
        sender.style.backgroundColor = "#ffffff";
    });

    //avata image box
    c = createPanel($g(hd+"panel12"), hd+"panel13", "", 16, 9, 50, 50);
    root.v_userPhoto = c;
    if (userPhoto == "") {
        ee.setImage(c,"/twitterG/main/user.png","trim","","")
    } else {
        ee.setImage(c,"url:" + g5.imgUrl + userPhoto,"trim","","")
    }
    c.v_username = username;
    c.style.cursor="pointer";
    c.style.borderRadius = "50%";
    ee.setOnClick(c,function(sender,event) {
        showUserPage.userName=sender.v_username;
        showUserPage();
        window.event? window.event.cancelBubble = true : event.stopPropagation();
    });
    ee.setOnMouseEnter(c, function(sender,e) {
        sender.style.webkitFilter="brightness(80%)";
        sender.style.filter="brightness(80%)";
    });
    ee.setOnMouseLeave(c, function(sender,e) {
        sender.style.webkitFilter="brightness(100%)";
        sender.style.filter="brightness(100%)";
    });

    var mainWidth = parseInt((w-75)*0.97);

    //v_titleText
    c = createPanel($g(hd+"panel12"), hd+"panel16", "", 75, 6, mainWidth, 26);
    root.v_titleText = c;
    addText(c, "<b>" + nickname + "</b> <font color='#808080' style='font-size:12pt'>@" + username + " · " + times + "</font>", "left", "middle", "微软雅黑", 12, "000000", "normal");
    c.v_username = username;
    c.style.cursor="pointer";
    ee.setOnClick(c,function(sender,event) {
        showUserPage.userName=sender.v_username;
        showUserPage();
        window.event? window.event.cancelBubble = true : event.stopPropagation();
    });
    ee.setOnMouseEnter(c,function(sender,e){
        sender.label.rows[0].cells[0].childNodes[0].style.textDecoration="underline";
    });
    ee.setOnMouseLeave(c,function(sender,e){
        sender.label.rows[0].cells[0].childNodes[0].style.textDecoration="";
    });

    //button
    if (username==g5.userName) {
    c = createPanel(root,hd+"button1", "", w-45, 6, 30, 30);
    ee.setImage(c,"url:/res/empty.png","stretch","","")
    c = createPanel($g(hd+"button1"), hd+"button1status1", "", 22, 20, 40, 39);
    addText(c, "<", "center", "middle", "微软雅黑", 14, "000000", "bold");
    eb.setDock(c,"fill","fill");
    c=$g(hd+"button1");
    c.status1=$g(hd+"button1status1");
    c.status1.button=c;
    c = createPanel($g(hd+"button1"), hd+"button1status2", "", 22, 20, 40, 39);
    addSkin(c, "/twitterG/skins/LRBtn12.png","LR3x",65,50,24,0,17,50,1,3,63,43);
    addText(c, "<", "center", "middle", "微软雅黑", 14, "000000", "bold");
    eb.setDock(c,"fill","fill");
    c=$g(hd+"button1");
    c.status2=$g(hd+"button1status2");
    c.status2.button=c;
    c = createPanel($g(hd+"button1"), hd+"button1status3", "", 22, 20, 40, 39);
    addSkin(c, "/twitterG/skins/twitterG008.png","LR3x",65,50,24,0,17,50,24,3,17,43);
    addText(c, "<", "center", "middle", "微软雅黑", 14, "0000C0", "bold");
    eb.setDock(c,"fill","fill");
    c=$g(hd+"button1");
    c.status3=$g(hd+"button1status3");
    c.status3.button=c;
    c = createPanel($g(hd+"button1"), hd+"button1clickLayer", "", 22, 20, 40, 39);
    ee.setImage(c,"url:/res/empty.png","stretch","","")
    eb.setDock(c,"fill","fill");
    ee.setOnClick(c,function(sender,event) {
        ee.setVisible(ee.getControl("panel40"), true);
        var e = event || window.event;
        ee.setRect(ee.getControl("panel149"), e.clientX - eb.getDivRect(ee.getControl("panel149")).w +20, e.clientY - 20, null, null);
