    //v_text
    c = createPanel($g(hd+"panel12"), hd+"panel17", "", 75, 26, mainWidth, w*0.0547667342799189);
    root.v_text = c;
    addText(c, "<font style='line-height:1.2;'>" + text + "</font>", "left", "top", "微软雅黑", 12, "000000", "normal");
    //eb.setFontSizeAuto(c, 25);
    eb.setDock(c,"disabled","disabled");
    eb.processDock();
    var rect = eb.getDivRect(c.label);
    ee.setRect(c, null, null, null, rect.h);
    rect = eb.getDivRect(c);
    var cy = rect.y + rect.h + 1;

    //photo1
    if (photo1 != "") {
        c = createPanel($g(hd+"panel12"), hd+"panel18", "", 75, cy, mainWidth, parseInt(w*0.474645030425964));
        c.style.borderRadius = "15px";
        c.style.borderWidth = "1px";
        c.style.borderColor = "rgb(204,214,221)";
        c.style.borderStyle = "solid";

        cy += w*0.474645030425964 + w*0.03;
        //c = createPanel($g(hd+"panel18"), hd+"panel19", "", 0, 0, 415, 234);
        if (photo1.substr(0,4) == "?YTB") {
            c.innerHTML = "<iframe width='" + mainWidth + "' height='" + parseInt(w*0.474645030425964) + "' src='https://www.youtube.com/embed/" +
                photo1.substr(4, photo1.length) + 
                "' frameborder='0' allow='accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture' allowfullscreen></iframe>";
            cy -= w*0.03;
            c = createPanel($g(hd+"panel12"), hd+"panel181", "", 75, cy, mainWidth, w*0.054);
            cy += w*0.054 + w*0.03;
            var temp = "https://www.youtube.com/watch?v=" + photo1.substr(4, photo1.length);
            addText(c, temp, "left", "center", "arial", 9, "808080", "normal");
            c.style.cursor = "pointer";
            ee.setOnClick(c,function(sender,event) {
                window.open(temp);
            });
        } else {
            c.imgUrl = "url:" + g5.imgUrl + photo1;
            ee.setImage(c,c.imgUrl,"trim","","");
            ee.setOnClick(c,function(sender,event) {
                ee.setVisible(ee.getControl("panel112"), true);
                ee.showAni(ee.getControl("panel142"), 0);
                ee.setImage(ee.getControl("panel145"),sender.imgUrl,"fill","","");
                window.event? window.event.cancelBubble = true : event.stopPropagation();
            });
        }
    }

    if (reUserName != "") {
        var p1ratio = 2.64;
        c = createPanel($g(hd+"panel12"), hd+"panel2", "", 75, cy, mainWidth-2, 100);
        c.style.borderRadius = "15px";
        //c.style.pointerEvents = "none";
        c.style.borderWidth = "1px";
        c.style.borderColor = "rgb(204,214,221)";
        c.style.borderStyle = "solid";
        ee.setOnMouseEnter(c, function(sender,e){
            sender.style.backgroundColor = "rgb(237,240,242)";
        });
        ee.setOnMouseLeave(c, function(sender,e){
            sender.style.backgroundColor = "";
        });

        var p1w = eb.getDivRect(c).w;
        var p1h = p1w / p1ratio;
        c = createPanel($g(hd+"panel2"), hd+"panel6", "", 0, 0, p1w, 100);
        eb.setDock(c,"disabled","disabled");
        //rePhoto
        c = createPanel($g(hd+"panel6"), hd+"panel8", "", 16, 12, 20, 20);
        if (rePhoto == "") {
            ee.setImage(c,"/twitterG/main/user.png","trim","","")
        } else {
            ee.setImage(c,"url:" + g5.imgUrl + rePhoto,"trim","","")
        }
        c.style.borderRadius = "50%";
        c.v_username = reUserName;
        c.style.cursor="pointer";
        ee.setOnClick(c,function(sender,event) {
            showUserPage.userName=sender.v_username;
            showUserPage();
            window.event? window.event.cancelBubble = true : event.stopPropagation();
        });
        //reUserName,reNickName,reTime
        c = createPanel($g(hd+"panel6"), hd+"panel11", "", 45, 8, p1w*0.833734939759036, 28);
        addText(c, "<b>" + reNickName + "</b> <font color='#808080' style='font-size:12pt'>@" + reUserName + " · " + reTimes + "</font>", "left", "middle", "微软雅黑", 12, "000000", "normal");
        c.v_username = reUserName;
        c.style.cursor="pointer";
