    //https://www.youtube.com/watch?v=yJG3WMG1dak
    g5.newPhoto1 = url.substr(pos + "youtube.com/watch?v=".length, url.length);
} else if ((pos=url.toLowerCase().indexOf("youtu.be/",0))>0) {
    //https://youtu.be/edESgyDUKDU
    g5.newPhoto1 = url.substr(pos + "youtu.be/".length, url.length);
} else {
    alert("您输入的视频地址不正确");
    return;
}
if ((g5.newPhoto1.length<=8)||(g5.newPhoto1.length>=14)) {
    alert("您输入的视频地址不正确");
    return;
}
var rect = eb.getDivRect(ee.getControl("panel52"));
url = "<iframe width='" + rect.w + "' height='" + rect.h + "' src='https://www.youtube.com/embed/" +
    g5.newPhoto1 + 
    "' frameborder='0' allow='accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture' allowfullscreen></iframe>";
